    # Initialize the result with the first operand
    result = operand[0]

    # Iterate over the operator list and the operand list starting from the second operand
    for op, num in zip(operator, operand[1:]):
        # Perform the operation based on the operator
        if op == '+':
            result += num
        elif op == '-':
            result -= num
        elif op == '*':
            result *= num
        # Perform floor division
        elif op == '//':
            result //= num
        elif op == '**':
            result **= num
        else:
