def match_parens(lst):
    """
    Check if it's possible to concatenate two strings of parentheses in some order
    to get a balanced string.

    Args:
        lst (list): A list of two strings, both strings consist of open parentheses '(' or close parentheses ')'.

    Returns:
        str: 'Yes' if there's a way to make a good string, 'No' otherwise.
    """
    # Initialize a stack to store opening parentheses
    stack = []

    # Process the first string
    for char in lst[0]:
        # If the character is an opening parenthesis, push it into the stack
        if char == '(':
            stack.append(char)
        # If the character is a closing parenthesis, pop the stack if it's not empty
        elif char == ')':
            if stack:
                stack.pop()
            else:
                return 'No'

    # Process the second string
    for char in lst[1]:
        # If the character is an opening parenthesis, push it into the stack
        if char == '(':
            stack.append(char)
        # If the character is a closing parenthesis, pop the stack if it's not empty
        elif char == ')':
            if stack:
                stack.pop()
            else:
                return 'No'

    # If the stack is not empty at the end, return 'No'
    if stack:
        return 'No'
    else:
        return 'Yes'

from datetime import datetime

def valid_date(date):
    """
    Validates a given date string.

    Args:
    date (str): The date string to be validated.

    Returns:
    bool: True if the date is valid, False otherwise.
    """
    # Check if the date string is not empty
    if not date:
        return False

    # Check if the date string is in the correct format
    try:
        datetime.strptime(date, '%m-%d-%Y')
    except ValueError:
        return False

    # Extract the month and day from the date string
    month, day, year = map(int, date.split('-'))

    # Check if the month is valid
    if not 1 <= month <= 12:
        return False

    # Check if the day is valid based on the month
    if month in [1, 3, 3, 5, 7, 8, 10, 12]:
        if not 1 <= day <= 31:
            return False
    elif month in [4, 6, 9, 11]:
        if not 1 <= day <= 30:
            return False
    elif month == 2:
        if not 1 <= day <= 29:
            return False

    # If all checks pass, the date is valid
    return True

def is_equal_to_sum_even(n):
    """Evaluate whether the given number n can be written as the sum of exactly 4 positive even numbers
    Example
    is_equal_to_sum_even(4) == False
    is_equal_to_sum_even(6) == False
    is_equal_to_sum_even(8) == True
    """
    if n % 2 != 0:
        return False
    for i in range(n // 2, 0, -1):
        if i % 2 == 0 and (n - 2 * i) % 2 == 0 and (n - 2 * i) >= 2 * i:
            return True
    return False
